		if($data['rights_holder'] == empty_lookup_value){ $data['rights_holder'] = ''; }
	$data['data_evaluation'] = makeSafe($_POST['data_evaluation']);
		if($data['data_evaluation'] == empty_lookup_value){ $data['data_evaluation'] = ''; }
	$data['authority_record'] = makeSafe($_POST['authority_record']);
		if($data['authority_record'] == empty_lookup_value){ $data['authority_record'] = ''; }
	$data['authority_organization'] = makeSafe($_POST['authority_organization']);
		if($data['authority_organization'] == empty_lookup_value){ $data['authority_organization'] = ''; }
	$data['ext_source'] = makeSafe($_POST['ext_source']);
		if($data['ext_source'] == empty_lookup_value){ $data['ext_source'] = ''; }
	$data['selectedID']=makeSafe($selected_id);
	if($_POST['img_remove'] == 1){
		$data['img'] = '';
		// delete file from server
		$res = sql("select `img` from `biblio_doc` where `id`='".makeSafe($selected_id)."'", $eo);
		if($row=@db_fetch_row($res)){
			if($row[0]!=''){
				@unlink(getUploadDir('').$row[0]);
				preg_match('/^[a-z0-9_]+\.(gif|png|jpg|jpeg|jpe)$/i', $row[0], $m);
				$thumbDV=str_replace(".$m[1]ffffgggg", "_dv.$m[1]", $row[0].'ffffgggg');
				$thumbTV=str_replace(".$m[1]ffffgggg", "_tv.$m[1]", $row[0].'ffffgggg');
				@unlink(getUploadDir('').$thumbTV);
				@unlink(getUploadDir('').$thumbDV);
			}
		}
	}else{
		$data['img'] = PrepareUploadedFile('img', 102400, 'jpg|jpeg|gif|png', false, "");
		if($data['img']) createThumbnail($data['img'], getThumbnailSpecs('biblio_doc', 'img', 'tv'));
		if($data['img']) createThumbnail($data['img'], getThumbnailSpecs('biblio_doc', 'img', 'dv'));
		// delete file from server
		if($data['img'] != ''){
			$res = sql("select `img` from `biblio_doc` where `id`='".makeSafe($selected_id)."'", $eo);
			if($row=@db_fetch_row($res)){
				if($row[0]!=''){
					@unlink(getUploadDir('').$row[0]);
					preg_match('/^[a-z0-9_]+\.(gif|png|jpg|jpeg|jpe)$/i', $row[0], $m);
					$thumbDV=str_replace(".$m[1]ffffgggg", "_dv.$m[1]", $row[0].'ffffgggg');
					$thumbTV=str_replace(".$m[1]ffffgggg", "_tv.$m[1]", $row[0].'ffffgggg');
					@unlink(getUploadDir('').$thumbTV);
					@unlink(getUploadDir('').$thumbDV);
				}
			}
		}
	}
	if($_POST['pdf_book_remove'] == 1){
		$data['pdf_book'] = '';
		// delete file from server
		$res = sql("select `pdf_book` from `biblio_doc` where `id`='".makeSafe($selected_id)."'", $eo);
		if($row=@db_fetch_row($res)){
			if($row[0]!=''){
				@unlink(getUploadDir('').$row[0]);
			}
		}
	}else{
		$data['pdf_book'] = PrepareUploadedFile('pdf_book', 92160000, 'txt|doc|docx|docm|odt|pdf|rtf', false, "");
		// delete file from server
		if($data['pdf_book'] != ''){
			$res = sql("select `pdf_book` from `biblio_doc` where `id`='".makeSafe($selected_id)."'", $eo);
			if($row=@db_fetch_row($res)){
				if($row[0]!=''){
					@unlink(getUploadDir('').$row[0]);
				}
			}
		}
	}

	// hook: biblio_doc_before_update
	if(function_exists('biblio_doc_before_update')){
		$args=array();
		if(!biblio_doc_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `biblio_doc` set       ' . ($data['img']!='' ? "`img`='{$data['img']}'" : ($_POST['img_remove'] != 1 ? '`img`=`img`' : '`img`=NULL')) . ', `author_name`=' . (($data['author_name'] !== '' && $data['author_name'] !== NULL) ? "'{$data['author_name']}'" : 'NULL') . ', `author_id`=' . (($data['author_id'] !== '' && $data['author_id'] !== NULL) ? "'{$data['author_id']}'" : 'NULL') . ', `type`=' . (($data['type'] !== '' && $data['type'] !== NULL) ? "'{$data['type']}'" : 'NULL') . ', `genre`=' . (($data['genre'] !== '' && $data['genre'] !== NULL) ? "'{$data['genre']}'" : 'NULL') . ', `created`=' . (($data['created'] !== '' && $data['created'] !== NULL) ? "'{$data['created']}'" : 'NULL') . ', `published`=' . (($data['published'] !== '' && $data['published'] !== NULL) ? "'{$data['published']}'" : 'NULL') . ', `title`=' . (($data['title'] !== '' && $data['title'] !== NULL) ? "'{$data['title']}'" : 'NULL') . ', `subtitle`=' . (($data['subtitle'] !== '' && $data['subtitle'] !== NULL) ? "'{$data['subtitle']}'" : 'NULL') . ', `publisher`=' . (($data['publisher'] !== '' && $data['publisher'] !== NULL) ? "'{$data['publisher']}'" : 'NULL') . ', `location`=' . (($data['location'] !== '' && $data['location'] !== NULL) ? "'{$data['location']}'" : 'NULL') . ', `citation`=' . (($data['citation'] !== '' && $data['citation'] !== NULL) ? "'{$data['citation']}'" : 'NULL') . ', `description`=' . (($data['description'] !== '' && $data['description'] !== NULL) ? "'{$data['description']}'" : 'NULL') . ', `source`=' . (($data['source'] !== '' && $data['source'] !== NULL) ? "'{$data['source']}'" : 'NULL') . ', `medium`=' . (($data['medium'] !== '' && $data['medium'] !== NULL) ? "'{$data['medium']}'" : 'NULL') . ', `language`=' . (($data['language'] !== '' && $data['language'] !== NULL) ? "'{$data['language']}'" : 'NULL') . ', `format`=' . (($data['format'] !== '' && $data['format'] !== NULL) ? "'{$data['format']}'" : 'NULL') . ', `subject`=' . (($data['subject'] !== '' && $data['subject'] !== NULL) ? "'{$data['subject']}'" : 'NULL') . ', `rights`=' . (($data['rights'] !== '' && $data['rights'] !== NULL) ? "'{$data['rights']}'" : 'NULL') . ', `rights_holder`=' . (($data['rights_holder'] !== '' && $data['rights_holder'] !== NULL) ? "'{$data['rights_holder']}'" : 'NULL') . ', `data_evaluation`=' . (($data['data_evaluation'] !== '' && $data['data_evaluation'] !== NULL) ? "'{$data['data_evaluation']}'" : 'NULL') . ', `authority_record`=' . (($data['authority_record'] !== '' && $data['authority_record'] !== NULL) ? "'{$data['authority_record']}'" : 'NULL') . ', `authority_organization`=' . (($data['authority_organization'] !== '' && $data['authority_organization'] !== NULL) ? "'{$data['authority_organization']}'" : 'NULL') . ', ' . ($data['pdf_book']!='' ? "`pdf_book`='{$data['pdf_book']}'" : ($_POST['pdf_book_remove'] != 1 ? '`pdf_book`=`pdf_book`' : '`pdf_book`=NULL')) . ', `ext_source`=' . (($data['ext_source'] !== '' && $data['ext_source'] !== NULL) ? "'{$data['ext_source']}'" : 'NULL') . " where `id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="biblio_doc_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: biblio_doc_after_update
	if(function_exists('biblio_doc_after_update')){
		$res = sql("SELECT * FROM `biblio_doc` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id'];
		$args = array();
		if(!biblio_doc_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='biblio_doc' and pkValue='".makeSafe($selected_id)."'", $eo);

}

function biblio_doc_form($selected_id = '', $AllowUpdate = 1, $AllowInsert = 1, $AllowDelete = 1, $ShowCancel = 0){
	// function to return an editable form for a table records
	// and fill it with data of record whose ID is $selected_id. If $selected_id
	// is empty, an empty form is shown, with only an 'Add New'
	// button displayed.

	global $Translation;

	// mm: get table permissions
	$arrPerm=getTablePermissions('biblio_doc');
	if(!$arrPerm[1] && $selected_id==''){ return ''; }
	$AllowInsert = ($arrPerm[1] ? true : false);
	// print preview?
	$dvprint = false;
	if($selected_id && $_REQUEST['dvprint_x'] != ''){
		$dvprint = true;
	}

	$filterer_author_name = thisOr(undo_magic_quotes($_REQUEST['filterer_author_name']), '');
	$filterer_type = thisOr(undo_magic_quotes($_REQUEST['filterer_type']), '');
	$filterer_genre = thisOr(undo_magic_quotes($_REQUEST['filterer_genre']), '');
	$filterer_language = thisOr(undo_magic_quotes($_REQUEST['filterer_language']), '');
	$filterer_rights = thisOr(undo_magic_quotes($_REQUEST['filterer_rights']), '');
	$filterer_authority_organization = thisOr(undo_magic_quotes($_REQUEST['filterer_authority_organization']), '');

	// populate filterers, starting from children to grand-parents

	// unique random identifier
	$rnd1 = ($dvprint ? rand(1000000, 9999999) : '');
	// combobox: author_name
	$combo_author_name = new DataCombo;
	// combobox: type
	$combo_type = new DataCombo;
	// combobox: genre
	$combo_genre = new DataCombo;
	// combobox: language
	$combo_language = new DataCombo;
	// combobox: rights
	$combo_rights = new DataCombo;
	// combobox: data_evaluation
	$combo_data_evaluation = new Combo;
	$combo_data_evaluation->ListType = 0;
	$combo_data_evaluation->MultipleSeparator = ', ';
	$combo_data_evaluation->ListBoxHeight = 10;
	$combo_data_evaluation->RadiosPerLine = 1;
	if(is_file(dirname(__FILE__).'/hooks/biblio_doc.data_evaluation.csv')){
		$data_evaluation_data = addslashes(implode('', @file(dirname(__FILE__).'/hooks/biblio_doc.data_evaluation.csv')));
		$combo_data_evaluation->ListItem = explode('||', entitiesToUTF8(convertLegacyOptions($data_evaluation_data)));
		$combo_data_evaluation->ListData = $combo_data_evaluation->ListItem;
	}else{
		$combo_data_evaluation->ListItem = explode('||', entitiesToUTF8(convertLegacyOptions("none;; self-evaluation;; peer-review;; authority code")));
		$combo_data_evaluation->ListData = $combo_data_evaluation->ListItem;
	}
	$combo_data_evaluation->SelectName = 'data_evaluation';
	// combobox: authority_organization
	$combo_authority_organization = new DataCombo;

	if($selected_id){
		// mm: check member permissions
		if(!$arrPerm[2]){
			return "";
		}
		// mm: who is the owner?
		$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='biblio_doc' and pkValue='".makeSafe($selected_id)."'");
		$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='biblio_doc' and pkValue='".makeSafe($selected_id)."'");
		if($arrPerm[2]==1 && getLoggedMemberID()!=$ownerMemberID){
			return "";
		}
		if($arrPerm[2]==2 && getLoggedGroupID()!=$ownerGroupID){
			return "";
		}

		// can edit?
		if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){
			$AllowUpdate=1;
		}else{
			$AllowUpdate=0;
		}

		$res = sql("select * from `biblio_doc` where `id`='".makeSafe($selected_id)."'", $eo);
		if(!($row = db_fetch_array($res))){
			return error_message($Translation['No records found']);
		}
		$urow = $row; /* unsanitized data */
		$hc = new CI_Input();
		$row = $hc->xss_clean($row); /* sanitize data */
		$combo_author_name->SelectedData = $row['author_name'];
		$combo_type->SelectedData = $row['type'];
		$combo_genre->SelectedData = $row['genre'];
		$combo_language->SelectedData = $row['language'];
		$combo_rights->SelectedData = $row['rights'];
		$combo_data_evaluation->SelectedData = $row['data_evaluation'];
		$combo_authority_organization->SelectedData = $row['authority_organization'];
	}else{
		$combo_author_name->SelectedData = $filterer_author_name;
		$combo_type->SelectedData = $filterer_type;
		$combo_genre->SelectedData = $filterer_genre;
		$combo_language->SelectedData = $filterer_language;
