function biblio_trascript_insert(){
	global $Translation;

	if($_GET['insert_x']!=''){$_POST=$_GET;}

	// mm: can member insert record?
	$arrPerm=getTablePermissions('biblio_trascript');
	if(!$arrPerm[1]){
		return false;
	}

	$data['author'] = makeSafe($_POST['author']);
		if($data['author'] == empty_lookup_value){ $data['author'] = ''; }
	$data['bibliography_title'] = makeSafe($_POST['bibliography_title']);
		if($data['bibliography_title'] == empty_lookup_value){ $data['bibliography_title'] = ''; }
	$data['trascriber_memberID'] = makeSafe($_POST['trascriber_memberID']);
		if($data['trascriber_memberID'] == empty_lookup_value){ $data['trascriber_memberID'] = ''; }
	$data['transcript_title'] = makeSafe($_POST['transcript_title']);
		if($data['transcript_title'] == empty_lookup_value){ $data['transcript_title'] = ''; }
	$data['transcript'] = PrepareUploadedFile('transcript', 92160000,'txt|doc|docx|docm|odt|pdf|rtf', false, '');
	$data['credits'] = PrepareUploadedFile('credits', 102400,'txt|doc|docx|docm|odt|pdf|rtf', false, '');

	/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
	if($_REQUEST['SelectedID']){
		$res = sql("select * from biblio_trascript where id='" . makeSafe($_REQUEST['SelectedID']) . "'");
		if($row = db_fetch_assoc($res)){
			if(!$data['transcript']) $data['transcript'] = makeSafe($row['transcript']);
			if(!$data['credits']) $data['credits'] = makeSafe($row['credits']);
		}
	}

	// hook: biblio_trascript_before_insert
	if(function_exists('biblio_trascript_before_insert')){
		$args=array();
		if(!biblio_trascript_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('insert into `biblio_trascript` set       `author`=' . (($data['author'] !== '' && $data['author'] !== NULL) ? "'{$data['author']}'" : 'NULL') . ', `bibliography_title`=' . (($data['bibliography_title'] !== '' && $data['bibliography_title'] !== NULL) ? "'{$data['bibliography_title']}'" : 'NULL') . ', `trascriber_memberID`=' . (($data['trascriber_memberID'] !== '' && $data['trascriber_memberID'] !== NULL) ? "'{$data['trascriber_memberID']}'" : 'NULL') . ', `transcript_title`=' . (($data['transcript_title'] !== '' && $data['transcript_title'] !== NULL) ? "'{$data['transcript_title']}'" : 'NULL') . ', ' . ($data['transcript']!='' ? "`transcript`='{$data['transcript']}'" : ($_POST['transcript_remove'] != 1 ? '`transcript`=`transcript`' : '`transcript`=NULL')) . ', ' . ($data['credits']!='' ? "`credits`='{$data['credits']}'" : ($_POST['credits_remove'] != 1 ? '`credits`=`credits`' : '`credits`=NULL')), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"biblio_trascript_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID=db_insert_id(db_link());

	// hook: biblio_trascript_after_insert
	if(function_exists('biblio_trascript_after_insert')){
		$res = sql("select * from `biblio_trascript` where `id`='" . makeSafe($recID) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID);
		$args=array();
		if(!biblio_trascript_after_insert($data, getMemberInfo(), $args)){ return (get_magic_quotes_gpc() ? stripslashes($recID) : $recID); }
	}

	// mm: save ownership data
	sql("insert into membership_userrecords set tableName='biblio_trascript', pkValue='$recID', memberID='".getLoggedMemberID()."', dateAdded='".time()."', dateUpdated='".time()."', groupID='".getLoggedGroupID()."'", $eo);

	return (get_magic_quotes_gpc() ? stripslashes($recID) : $recID);
}

