function biblio_trascript_update($selected_id){
	global $Translation;

	if($_GET['update_x']!=''){$_POST=$_GET;}

	// mm: can member edit record?
	$arrPerm=getTablePermissions('biblio_trascript');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='biblio_trascript' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='biblio_trascript' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['author'] = makeSafe($_POST['author']);
		if($data['author'] == empty_lookup_value){ $data['author'] = ''; }
	$data['bibliography_title'] = makeSafe($_POST['bibliography_title']);
		if($data['bibliography_title'] == empty_lookup_value){ $data['bibliography_title'] = ''; }
	$data['trascriber_memberID'] = makeSafe($_POST['trascriber_memberID']);
		if($data['trascriber_memberID'] == empty_lookup_value){ $data['trascriber_memberID'] = ''; }
	$data['transcript_title'] = makeSafe($_POST['transcript_title']);
		if($data['transcript_title'] == empty_lookup_value){ $data['transcript_title'] = ''; }
	$data['selectedID']=makeSafe($selected_id);
	if($_POST['transcript_remove'] == 1){
		$data['transcript'] = '';
		// delete file from server
		$res = sql("select `transcript` from `biblio_trascript` where `id`='".makeSafe($selected_id)."'", $eo);
		if($row=@db_fetch_row($res)){
			if($row[0]!=''){
				@unlink(getUploadDir('').$row[0]);
			}
		}
	}else{
		$data['transcript'] = PrepareUploadedFile('transcript', 92160000, 'txt|doc|docx|docm|odt|pdf|rtf', false, "");
		// delete file from server
		if($data['transcript'] != ''){
			$res = sql("select `transcript` from `biblio_trascript` where `id`='".makeSafe($selected_id)."'", $eo);
			if($row=@db_fetch_row($res)){
				if($row[0]!=''){
					@unlink(getUploadDir('').$row[0]);
				}
			}
		}
	}
	if($_POST['credits_remove'] == 1){
		$data['credits'] = '';
	}else{
		$data['credits'] = PrepareUploadedFile('credits', 102400, 'txt|doc|docx|docm|odt|pdf|rtf', false, "");
	}

	// hook: biblio_trascript_before_update
	if(function_exists('biblio_trascript_before_update')){
		$args=array();
		if(!biblio_trascript_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `biblio_trascript` set       `author`=' . (($data['author'] !== '' && $data['author'] !== NULL) ? "'{$data['author']}'" : 'NULL') . ', `bibliography_title`=' . (($data['bibliography_title'] !== '' && $data['bibliography_title'] !== NULL) ? "'{$data['bibliography_title']}'" : 'NULL') . ', `trascriber_memberID`=' . (($data['trascriber_memberID'] !== '' && $data['trascriber_memberID'] !== NULL) ? "'{$data['trascriber_memberID']}'" : 'NULL') . ', `transcript_title`=' . (($data['transcript_title'] !== '' && $data['transcript_title'] !== NULL) ? "'{$data['transcript_title']}'" : 'NULL') . ', ' . ($data['transcript']!='' ? "`transcript`='{$data['transcript']}'" : ($_POST['transcript_remove'] != 1 ? '`transcript`=`transcript`' : '`transcript`=NULL')) . ', ' . ($data['credits']!='' ? "`credits`='{$data['credits']}'" : ($_POST['credits_remove'] != 1 ? '`credits`=`credits`' : '`credits`=NULL')) . " where `id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="biblio_trascript_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: biblio_trascript_after_update
	if(function_exists('biblio_trascript_after_update')){
		$res = sql("SELECT * FROM `biblio_trascript` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id'];
		$args = array();
		if(!biblio_trascript_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='biblio_trascript' and pkValue='".makeSafe($selected_id)."'", $eo);

}

function biblio_trascript_form($selected_id = '', $AllowUpdate = 1, $AllowInsert = 1, $AllowDelete = 1, $ShowCancel = 0){
	// function to return an editable form for a table records
	// and fill it with data of record whose ID is $selected_id. If $selected_id
	// is empty, an empty form is shown, with only an 'Add New'
	// button displayed.

	global $Translation;

	// mm: get table permissions
	$arrPerm=getTablePermissions('biblio_trascript');
	if(!$arrPerm[1] && $selected_id==''){ return ''; }
	$AllowInsert = ($arrPerm[1] ? true : false);
	// print preview?
	$dvprint = false;
	if($selected_id && $_REQUEST['dvprint_x'] != ''){
		$dvprint = true;
	}

	$filterer_author = thisOr(undo_magic_quotes($_REQUEST['filterer_author']), '');
	$filterer_bibliography_title = thisOr(undo_magic_quotes($_REQUEST['filterer_bibliography_title']), '');

	// populate filterers, starting from children to grand-parents
	if($filterer_bibliography_title && !$filterer_author) $filterer_author = sqlValue("select author_name from biblio_doc where id='" . makeSafe($filterer_bibliography_title) . "'");

	// unique random identifier
	$rnd1 = ($dvprint ? rand(1000000, 9999999) : '');
	// combobox: author
	$combo_author = new DataCombo;
	// combobox: bibliography_title, filterable by: author
	$combo_bibliography_title = new DataCombo;

	if($selected_id){
		// mm: check member permissions
		if(!$arrPerm[2]){
			return "";
		}
		// mm: who is the owner?
		$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='biblio_trascript' and pkValue='".makeSafe($selected_id)."'");
		$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='biblio_trascript' and pkValue='".makeSafe($selected_id)."'");
		if($arrPerm[2]==1 && getLoggedMemberID()!=$ownerMemberID){
			return "";
		}
		if($arrPerm[2]==2 && getLoggedGroupID()!=$ownerGroupID){
			return "";
		}

		// can edit?
		if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){
			$AllowUpdate=1;
		}else{
			$AllowUpdate=0;
		}

		$res = sql("select * from `biblio_trascript` where `id`='".makeSafe($selected_id)."'", $eo);
		if(!($row = db_fetch_array($res))){
			return error_message($Translation['No records found']);
		}
		$urow = $row; /* unsanitized data */
		$hc = new CI_Input();
		$row = $hc->xss_clean($row); /* sanitize data */
		$combo_author->SelectedData = $row['author'];
		$combo_bibliography_title->SelectedData = $row['bibliography_title'];
	}else{
		$combo_author->SelectedData = $filterer_author;
		$combo_bibliography_title->SelectedData = $filterer_bibliography_title;
	}
	$combo_author->HTML = '<span id="author-container' . $rnd1 . '"></span><input type="hidden" name="author" id="author' . $rnd1 . '">';
	$combo_author->MatchText = '<span id="author-container-readonly' . $rnd1 . '"></span><input type="hidden" name="author" id="author' . $rnd1 . '">';
	$combo_bibliography_title->HTML = '<span id="bibliography_title-container' . $rnd1 . '"></span><input type="hidden" name="bibliography_title" id="bibliography_title' . $rnd1 . '">';
	$combo_bibliography_title->MatchText = '<span id="bibliography_title-container-readonly' . $rnd1 . '"></span><input type="hidden" name="bibliography_title" id="bibliography_title' . $rnd1 . '">';

	ob_start();
	?>

	<script>
		// initial lookup values
