		$templateCode=str_replace('<%%VALUE(concern)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(concern)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(sequence)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(sequence)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(issue)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(issue)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(theme)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(theme)%%>', urlencode(''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode=str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode=str_replace('<%%', '<!-- ', $templateCode);
	$templateCode=str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_POST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('[id^=\"{$name}_plink\"]').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode=preg_replace('/blank.gif" rel="lightbox\[.*?\]"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	// hook: storyweaving_scenes_dv
	if(function_exists('storyweaving_scenes_dv')){
		$args=array();
		storyweaving_scenes_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
